mapper.update:
UPDATE BOARD SET BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUM = #{boardNum}

UPDATE BOARD SET BOARD_HIT = BOARD_HIT +1 WHERE
BOARD_NUM =
#{num}

mapper.select:
SELECT
 COUNT(MEMBER_NUM) FROM BOARDGOOD WHERE
	BOARD_NUM=#{boardNum}

CASE WHEN COUNT(MEMBER_NUM)>0 THEN 1 ELSE 0 END AS ISGOOD
	FROM BOARDGOOD
	WHERE BOARD_NUM=#{boardNum} AND MEMBER_NUM=#{memberNum}

mapper.delete:
DELETE FROM BOARDGOOD WHERE MEMBER_NUM=#{memberNum}
AND
BOARD_NUM=#{boardNum}
